def selection_sort(arr):
    i = 0
    while i < len(arr):
        z = i + 1
        while z < len(arr):
            if arr[z] < arr[i]:
                temp = arr[i]
                arr[i] = arr[z]
                arr[z] = temp
            z += 1
        i += 1
    return arr

def selection_sort(arr):
    i = len(arr)
    while i > 0:
        i -= 1
        z = i
        while z > 0:
            z -= 1
            if arr[z] < arr[i]:
                temp = arr[i]
                arr[i] = arr[z]
                arr[z] = temp
    return arr
