def give_count(arr, target):
    count_value = 0
    for element in arr:
        if element == target:
            count_value += 1
    return count_value```

def give_count(arr, target):
    count_value = 0
    q = -1
    while q < len(arr) - 1:
        q += 1
        if arr[q] == target:
            count_value += 1
    return count_value
