def give_count(arr, target):
    count_value = 0
    for element in arr:
        if element == target:
            count_value += 1
    return count_value```

def give_count(arr, target):
    count_value = 0
    c = len(arr)
    while c > 0:
        c -= 1
        if arr[c] == target:
            count_value += 1
    return count_value
