def give_member(arr, id):
    member_value = -1
    g = 0
    while g < len(arr):
        if g == id:
            member_value = arr[g]
        g += 1
    return member_value

def give_member(arr, id):
    member_value = -1
    g = 0
    while g <= len(arr) - 1:
        if g == id:
            member_value = arr[g]
        g += 1
    return member_value
