def give_count(arr, target):
    count_value = 0
    e = 0
    while e < len(arr):
        if arr[e] == target:
            count_value += 1
        e += 1
    return count_value

def give_count(arr, target):
    count_value = 0
    e = 0
    while e <= len(arr) - 1:
        if arr[e] == target:
            count_value += 1
        e += 1
    return count_value
