def give_count(arr, target):
    count_value = 0
    r = 0
    while r < len(arr):
        if arr[r] == target:
            count_value += 1
        r += 1
    return count_value

def give_count(arr, target):
    count_value = 0
    r = 0
    while r <= len(arr) - 1:
        if arr[r] == target:
            count_value += 1
        r += 1
    return count_value
