def find_max(arr):
    max_value = arr[0]
    b = 0
    while b < len(arr):
        if arr[b] > max_value:
            max_value = arr[b]
        b += 1
    return max_value

def find_max(arr):
    max_value = arr[0]
    b = -1
    while b < len(arr) - 1:
        b += 1
        if arr[b] > max_value:
            max_value = arr[b]
    return max_value
