def give_count(arr, target):
    count_value = 0
    k = 0
    while k < len(arr):
        if arr[k] == target:
            count_value += 1
        k += 1
    return count_value

def give_count(arr, target):
    count_value = 0
    k = -1
    while k < len(arr) - 1:
        k += 1
        if arr[k] == target:
            count_value += 1
    return count_value
