def find_max(arr):
    max_value = arr[0]
    g = 0
    while g < len(arr):
        if arr[g] > max_value:
            max_value = arr[g]
        g += 1
    return max_value

def find_max(arr):
    max_value = arr[0]
    g = -1
    while g < len(arr) - 1:
        g += 1
        if arr[g] > max_value:
            max_value = arr[g]
    return max_value
