def find_max(arr):
    max_value = arr[0]
    h = 0
    while h < len(arr):
        if arr[h] > max_value:
            max_value = arr[h]
        h += 1
    return max_value

def find_max(arr):
    max_value = arr[0]
    h = -1
    while h < len(arr) - 1:
        h += 1
        if arr[h] > max_value:
            max_value = arr[h]
    return max_value
