def find_max(arr):
    max_value = arr[0]
    q = 0
    while q < len(arr):
        if arr[q] > max_value:
            max_value = arr[q]
        q += 1
    return max_value

def find_max(arr):
    max_value = arr[-1]
    q = len(arr)
    while q > 0:
        q -= 1
        if arr[q] > max_value:
            max_value = arr[q]
    return max_value
