def find_min(arr):
    min_value = arr[0]
    n = 0
    while n < len(arr):
        if arr[n] < min_value:
            min_value = arr[n]
        n += 1
    return min_value

def find_min(arr):
    min_value = arr[-1]
    n = len(arr)
    while n > 0:
        n -= 1
        if arr[n] < min_value:
            min_value = arr[n]
    return min_value
