def give_count(arr, target):
    count_value = 0
    h = 0
    while h < len(arr):
        if arr[h] == target:
            count_value += 1
        h += 1
    return count_value

def give_count(arr, target):
    count_value = 0
    h = len(arr)
    while h > 0:
        h -= 1
        if arr[h] == target:
            count_value += 1
    return count_value
