def selection_sort(arr):
    o = -1
    while o < len(arr) - 1:
        o += 1
        z = o + 1
        while z < len(arr):
            if arr[z] < arr[o]:
                temp = arr[o]
                arr[o] = arr[z]
                arr[z] = temp
            z += 1
    return arr
