def give_member(arr, id):
    member_value = -1
    c = 0
    while c < len(arr):
        if c == id:
            member_value = arr[c]
        c += 1
    return member_value

def give_member(arr, id):
    member_value = -1
    c = 0
    while c <= len(arr) - 1:
        if c == id:
            member_value = arr[c]
        c += 1
    return member_value
