def find_max(arr):
    max_value = arr[0]
    p = 0
    while p < len(arr):
        if arr[p] > max_value:
            max_value = arr[p]
        p += 1
    return max_value

def find_max(arr):
    max_value = arr[0]
    p = 0
    while p <= len(arr) - 1:
        if arr[p] > max_value:
            max_value = arr[p]
        p += 1
    return max_value
