def selection_sort(arr):
    d = 0
    while d < len(arr):
        z = d + 1
        while z < len(arr):
            if arr[z] < arr[d]:
                temp = arr[d]
                arr[d] = arr[z]
                arr[z] = temp
            z += 1
        d += 1
    return arr

def selection_sort(arr):
    d = 0
    while d <= len(arr) - 1:
        z = d + 1
        while z <= len(arr) - 1:
            if arr[z] < arr[d]:
                temp = arr[d]
                arr[d] = arr[z]
                arr[z] = temp
            z += 1
        d += 1
    return arr
