def find_max(arr):
    max_value = arr[0]
    t = 0
    while t < len(arr):
        if arr[t] > max_value:
            max_value = arr[t]
        t += 1
    return max_value

def find_max(arr):
    max_value = arr[0]
    t = 0
    while t <= len(arr) - 1:
        if arr[t] > max_value:
            max_value = arr[t]
        t += 1
    return max_value
