def selection_sort(arr):
    n = 0
    while n < len(arr):
        z = n + 1
        while z < len(arr):
            if arr[z] < arr[n]:
                temp = arr[n]
                arr[n] = arr[z]
                arr[z] = temp
            z += 1
        n += 1
    return arr

def selection_sort(arr):
    n = 0
    while n <= len(arr) - 1:
        z = n + 1
        while z <= len(arr) - 1:
            if arr[z] < arr[n]:
                temp = arr[n]
                arr[n] = arr[z]
                arr[z] = temp
            z += 1
        n += 1
    return arr
