def find_max(arr):
    max_value = arr[0]
    d = 0
    while d < len(arr):
        if arr[d] > max_value:
            max_value = arr[d]
        d += 1
    return max_value

def find_max(arr):
    max_value = arr[0]
    d = 0
    while d <= len(arr) - 1:
        if arr[d] > max_value:
            max_value = arr[d]
        d += 1
    return max_value
