def give_member(arr, id):
    member_value = -1
    h = 0
    while h < len(arr):
        if h == id:
            member_value = arr[h]
        h += 1
    return member_value

def give_member(arr, id):
    member_value = -1
    h = -1
    while h < len(arr) - 1:
        h += 1
        if h == id:
            member_value = arr[h]
    return member_value
