def give_member(arr, id):
    member_value = -1
    i = 0
    while i < len(arr):
        if i == id:
            member_value = arr[i]
        i += 1
    return member_value

def give_member(arr, id):
    member_value = -1
    i = -1
    while i < len(arr) - 1:
        i += 1
        if i == id:
            member_value = arr[i]
    return member_value
