def find_max(arr):
    max_value = arr[0]
    o = 0
    while o < len(arr):
        if arr[o] > max_value:
            max_value = arr[o]
        o += 1
    return max_value

def find_max(arr):
    max_value = arr[0]
    o = -1
    while o < len(arr) - 1:
        o += 1
        if arr[o] > max_value:
            max_value = arr[o]
    return max_value
