def selection_sort(arr):
    h = 0
    while h < len(arr):
        z = h + 1
        while z < len(arr):
            if arr[z] < arr[h]:
                temp = arr[h]
                arr[h] = arr[z]
                arr[z] = temp
            z += 1
        h += 1
    return arr

def selection_sort(arr):
    h = -1
    while h < len(arr) - 1:
        h += 1
        z = h + 1
        while z < len(arr):
            if arr[z] < arr[h]:
                temp = arr[h]
                arr[h] = arr[z]
                arr[z] = temp
            z += 1
    return arr
