def selection_sort(arr):
    q = 0
    while q < len(arr):
        z = q + 1
        while z < len(arr):
            if arr[z] < arr[q]:
                temp = arr[q]
                arr[q] = arr[z]
                arr[z] = temp
            z += 1
        q += 1
    return arr

def selection_sort(arr):
    q = -1
    while q < len(arr) - 1:
        q += 1
        z = q + 1
        while z < len(arr):
            if arr[z] < arr[q]:
                temp = arr[q]
                arr[q] = arr[z]
                arr[z] = temp
            z += 1
    return arr
