def give_count(arr, target):
    count_value = 0
    d = 0
    while d < len(arr):
        if arr[d] == target:
            count_value += 1
        d += 1
    return count_value

def give_count(arr, target):
    count_value = 0
    d = -1
    while d < len(arr) - 1:
        d += 1
        if arr[d] == target:
            count_value += 1
    return count_value
