def give_count(arr, target):
    count_value = 0
    t = 0
    while t < len(arr):
        if arr[t] == target:
            count_value += 1
        t += 1
    return count_value

def give_count(arr, target):
    count_value = 0
    t = -1
    while t < len(arr) - 1:
        t += 1
        if arr[t] == target:
            count_value += 1
    return count_value
