def find_max(arr):
    max_value = arr[0]
    c = 0
    while c < len(arr):
        if arr[c] > max_value:
            max_value = arr[c]
        c += 1
    return max_value

def find_max(arr):
    max_value = arr[-1]
    c = len(arr)
    while c > 0:
        c -= 1
        if arr[c] > max_value:
            max_value = arr[c]
    return max_value
