def find_min(arr):
    min_value = arr[0]
    g = 0
    while g < len(arr):
        if arr[g] < min_value:
            min_value = arr[g]
        g += 1
    return min_value

def find_min(arr):
    min_value = arr[-1]
    g = len(arr)
    while g > 0:
        g -= 1
        if arr[g] < min_value:
            min_value = arr[g]
    return min_value
