def find_min(arr):
    min_value = arr[0]
    l = 0
    while l < len(arr):
        if arr[l] < min_value:
            min_value = arr[l]
        l += 1
    return min_value

def find_min(arr):
    min_value = arr[-1]
    l = len(arr)
    while l > 0:
        l -= 1
        if arr[l] < min_value:
            min_value = arr[l]
    return min_value
