def give_count(arr, target):
    count_value = 0
    s = 0
    while s < len(arr):
        if arr[s] == target:
            count_value += 1
        s += 1
    return count_value

def give_count(arr, target):
    count_value = 0
    s = len(arr)
    while s > 0:
        s -= 1
        if arr[s] == target:
            count_value += 1
    return count_value
