def give_count(arr, target):
    count_value = 0
    n = 0
    while n < len(arr):
        if arr[n] == target:
            count_value += 1
        n += 1
    return count_value

def give_count(arr, target):
    count_value = 0
    n = 0
    while n <= len(arr) - 1:
        if arr[n] == target:
            count_value += 1
        n += 1
    return count_value
