def give_count(arr, target):
    count_value = 0
    p = 0
    while p < len(arr):
        if arr[p] == target:
            count_value += 1
        p += 1
    return count_value

def give_count(arr, target):
    count_value = 0
    p = -1
    while p < len(arr) - 1:
        p += 1
        if arr[p] == target:
            count_value += 1
    return count_value
