def give_count(arr, target):
    count_value = 0
    l = 0
    while l < len(arr):
        if arr[l] == target:
            count_value += 1
        l += 1
    return count_value

def give_count(arr, target):
    count_value = 0
    l = len(arr)
    while l > 0:
        l -= 1
        if arr[l] == target:
            count_value += 1
    return count_value
