def find_max(arr):
    max_value = arr[0]
    a = 0
    while a < len(arr):
        if arr[a] > max_value:
            max_value = arr[a]
        a += 1
    return max_value

def find_max(arr):
    max_value = arr[0]
    a = 0
    while a <= len(arr) - 1:
        if arr[a] > max_value:
            max_value = arr[a]
        a += 1
    return max_value
