def find_max(arr):
    max_value = arr[0]
    i = 0
    while i < len(arr):
        if arr[i] > max_value:
            max_value = arr[i]
        i += 1
    return max_value

def find_max(arr):
    max_value = arr[0]
    i = -1
    while i < len(arr) - 1:
        i += 1
        if arr[i] > max_value:
            max_value = arr[i]
    return max_value
