def give_member(arr, id):
    member_value = -1
    a = 0
    while a < len(arr):
        if a == id:
            member_value = arr[a]
        a += 1
    return member_value

def give_member(arr, id):
    member_value = -1
    a = len(arr)
    while a > 0:
        a -= 1
        if a == id:
            member_value = arr[a]
    return member_value
