def selection_sort(arr):
    a = 0
    while a < len(arr):
        z = a + 1
        while z < len(arr):
            if arr[z] < arr[a]:
                temp = arr[a]
                arr[a] = arr[z]
                arr[z] = temp
            z += 1
        a += 1
    return arr

