def selection_sort(arr):
    r = 0
    while r < len(arr):
        z = r + 1
        while z < len(arr):
            if arr[z] < arr[r]:
                temp = arr[r]
                arr[r] = arr[z]
                arr[z] = temp
            z += 1
        r += 1
    return arr

def selection_sort(arr):
    r = 0
    while r <= len(arr) - 1:
        z = r + 1
        while z <= len(arr) - 1:
            if arr[z] < arr[r]:
                temp = arr[r]
                arr[r] = arr[z]
                arr[z] = temp
            z += 1
        r += 1
    return arr
