def give_count(arr, target):
    count_value = 0
    g = 0
    while g < len(arr):
        if arr[g] == target:
            count_value += 1
        g += 1
    return count_value

def give_count(arr, target):
    count_value = 0
    g = -1
    while g < len(arr) - 1:
        g += 1
        if arr[g] == target:
            count_value += 1
    return count_value
