def selection_sort(arr):
    f = 0
    while f < len(arr):
        z = f + 1
        while z < len(arr):
            if arr[z] < arr[f]:
                temp = arr[f]
                arr[f] = arr[z]
                arr[z] = temp
            z += 1
        f += 1
    return arr

def selection_sort(arr):
    f = -1
    while f < len(arr) - 1:
        f += 1
        z = f + 1
        while z < len(arr):
            if arr[z] < arr[f]:
                temp = arr[f]
                arr[f] = arr[z]
                arr[z] = temp
            z += 1
    return arr
