def find_max(arr):
    max_value = arr[0]
    j = 0
    while j < len(arr):
        if arr[j] > max_value:
            max_value = arr[j]
        j += 1
    return max_value

def find_max(arr):
    max_value = arr[0]
    j = -1
    while j < len(arr) - 1:
        j += 1
        if arr[j] > max_value:
            max_value = arr[j]
    return max_value
