def give_count(arr, target):
    count_value = 0
    c = 0
    while c < len(arr):
        if arr[c] == target:
            count_value += 1
        c += 1
    return count_value

def give_count(arr, target):
    count_value = 0
    c = -1
    while c < len(arr) - 1:
        c += 1
        if arr[c] == target:
            count_value += 1
    return count_value
