def find_max(arr):
    max_value = arr[0]
    k = 0
    while k < len(arr):
        if arr[k] > max_value:
            max_value = arr[k]
        k += 1
    return max_value

def find_max(arr):
    max_value = arr[0]
    k = -1
    while k < len(arr) - 1:
        k += 1
        if arr[k] > max_value:
            max_value = arr[k]
    return max_value
