def give_count(arr, target):
    count_value = 0
    a = 0
    while a < len(arr):
        if arr[a] == target:
            count_value += 1
        a += 1
    return count_value

def give_count(arr, target):
    count_value = 0
    a = -1
    while a < len(arr) - 1:
        a += 1
        if arr[a] == target:
            count_value += 1
    return count_value
