def find_min(arr):
    min_value = arr[0]
    a = 0
    while a < len(arr):
        if arr[a] < min_value:
            min_value = arr[a]
        a += 1
    return min_value

def find_min(arr):
    min_value = arr[-1]
    a = len(arr)
    while a > 0:
        a -= 1
        if arr[a] < min_value:
            min_value = arr[a]
    return min_value
