def find_max(arr):
    max_value = arr[0]
    e = 0
    while e < len(arr):
        if arr[e] > max_value:
            max_value = arr[e]
        e += 1
    return max_value

def find_max(arr):
    max_value = arr[-1]
    e = len(arr)
    while e > 0:
        e -= 1
        if arr[e] > max_value:
            max_value = arr[e]
    return max_value
