def give_member(arr, id):
    member_value = -1
    e = 0
    while e < len(arr):
        if e == id:
            member_value = arr[e]
        e += 1
    return member_value

def give_member(arr, id):
    member_value = -1
    e = 0
    while e <= len(arr) - 1:
        if e == id:
            member_value = arr[e]
        e += 1
    return member_value
