def give_member(arr, id):
    member_value = -1
    j = 0
    while j < len(arr):
        if j == id:
            member_value = arr[j]
        j += 1
    return member_value

def give_member(arr, id):
    member_value = -1
    j = 0
    while j <= len(arr) - 1:
        if j == id:
            member_value = arr[j]
        j += 1
    return member_value
