def find_min(arr):
    min_value = arr[0]
    r = 0
    while r < len(arr):
        if arr[r] < min_value:
            min_value = arr[r]
        r += 1
    return min_value

def find_min(arr):
    min_value = arr[0]
    r = -1
    while r < len(arr) - 1:
        r += 1
        if arr[r] < min_value:
            min_value = arr[r]
    return min_value
